INSERT INTO `lc_basic` VALUES ('44', '关于我们', 'GuanYuWoMen', 'About/Index', '关于我们', '关于我们', '', '44', '<div class=\"general_con_c_ri_word\">\r\n	<span style=\"font-size:14px;color:#666666;\"><span>创驰云</span>网络是一家专业为中小企业提供电子商务策划、培训、应用于一体的大型联盟服\r\n务提供商，公司成立于2011年，几年来,公司已发展成为一家高速成长、富有活力且专业过硬的网络服务商。公司一致坚持以：做中国最诚信的网络服务提供\r\n商，只为企业提供最有效的网络营销解决方案为服务宗旨！让企业能有效利用互联网做好的网络营销，打造企业网络营销平台，提升企业形象、品牌，通过网络营销\r\n的方式开拓市场！</span> \r\n</div>\r\n<div class=\"general_con_c_ri_int\">\r\n	<span style=\"font-size:14px;color:#666666;\">以良心树立诚信人格，用思维创造崭新明天。</span> \r\n</div>', '2016-06-03 17:18:30', null, '4', '1');
INSERT INTO `lc_basic` VALUES ('45', '联系我们', 'LianXiWoMen', 'About/Contact', '联系我们', '联系我们', '', '44', '<p>\r\n	<span style=\"color:#666666;font-size:16px;\">公司地址：深圳市龙岗区龙翔大道9009号珠江国际中心18楼B1</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:16px;\">传真号码：0755-89360650</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:16px;\">邮政编码：518110</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:16px;\">工作时间：周一至周五 8：30 - 18：00</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:16px;\">7*24小时电话：135 7081 6465</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:16px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"weibo\">\r\n	<img alt=\"\" src=\"/Admin/template/Default/kindeditor/php/../attached/image/20160706/20160706172445_74245.png\" /> <img width=\"60\" alt=\"\" src=\"/Admin/template/Default/kindeditor/php/../attached/image/20160706/20160706172445_55087.png\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '2016-06-03 17:20:56', null, '1', '1');
