INSERT INTO `lc_web_config` VALUES ('1', '创驰云网络—专业APP开发', '', '龙岗网络营销策划,龙岗网站建设,深圳网络推广,龙岗系统开发,APP开发,移动开发网站设计,龙岗网站策划,深圳品牌网站,龙岗商城制作,创驰云网络,龙岗APP定制开发,深圳APP定制开发', '深圳市创驰云网络技术开发有限公司是专业移动互联网应用APP开发商。创驰云提供专业的企业级APP开发移动互联网开发解决方案。从客户创意到可视化APP原型图设计，从研发到质量控制，从APP上线发布到运营质保服务，创驰云APP开发工程师们保驾护航。咨询电话：13570816465', '深圳市龙岗区龙翔大道9009号珠江国际中心18楼B1', '', '0755-28837550', '0755-89360650', 'postmaster@ccynet.cn', '粤ICP备17057207号-1', '深圳市创驰云网络技术开发有限公司', 'web_up_file/web_logo_spic/20170215155720313.jpg', '', '1', '400-186-9596', '691977539|317361652', '1');
